class MainActivity : ComponentActivity() {
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)

        setContent {
            //todo lo de aca adentro se pinta en la pantalla
            PracticaJetpackComposeTheme {
                MyMessages(messages)
            }

        }
    }


}

@Composable
fun MyText(name: String, color: Color, style: androidx.compose.ui.text.TextStyle, lines: Int = Int.MAX_VALUE) {
    Text(text = name, color = color, style = style, maxLines = lines)
}

@Composable
fun MyTexts(message: MyMessage) {
    var expanded by remember { mutableStateOf(false) }
    Column(
        Modifier
            .padding(start = 8.dp)
            .clickable { //true o false
                expanded = !expanded
            }) {
        MyText(
            message.title,
            MaterialTheme.colors.primary,
            MaterialTheme.typography.subtitle1
        )
        Spacer(modifier = Modifier.height(16.dp))
        MyText(
            message.body,
            MaterialTheme.colors.onBackground,
            MaterialTheme.typography.subtitle2,
            if(expanded) Int.MAX_VALUE else 1
        )
    }

}

data class MyMessage(val title: String, val body: String)

@Composable
fun MyComponent(message: MyMessage) {
    Row(
        modifier = Modifier
            .background(MaterialTheme.colors.background)
            .padding(8.dp)
    ) {
        MyImage()
        Spacer(modifier = Modifier.width(16.dp))
        MyTexts(message)
    }
}

@Composable
fun MyImage() {
    Image(
        painterResource(id = R.drawable.ic_launcher_foreground),
        contentDescription = "imagen de prueba",
        modifier = Modifier
            .clip(CircleShape)
            .background(Color.Gray)
            .size(64.dp) //imagen redonda y color de fondo
            .background(MaterialTheme.colors.primary)
    )
}

@Composable
fun MyMessages(messages: List<MyMessage>){
    LazyColumn {
        items(messages){ message ->
            MyComponent(message)
        }
    }
}
